INSERT INTO BOARD (
	REF, STEP, B_LEVEL, NAME, SUBJ, NEWCONTENTS, HIT, REGDATE
) VALUES (
	#{ref}, '0', '0', #{name}, #{subj}, #{newcontents}, '0', sysdate()
)

	SELECT MAX(B_NO) AS B_NO FROM BOARD

