) VALUES (
	SEQ_TESTBOARDBNO.NEXTVAL, #{ref}, #{step}, #{b_level}+1, #{name}, #{subj}, #{newcontents}, '0', sysdate
)

	SELECT MAX(B_NO) AS B_NO FROM TESTBOARD
